import React, { Component } from 'react';
import './App.css';
import Header from "./components/Header";
import Container from "./components/Container";


class App extends Component {

    constructor(props) {
        super(props)

        // Bind the this context to the handler function
        this.handler = this.handler.bind(this);

        // Set some state
        this.state = {
            gameActive: true,
            score: 0,
            topScore: 0
        };
    }

    checkForDuplicates(key) {
      clickedItems.forEach(item => {
          if(item !== key) {
              // clicked item has NOT been clicked before
              console.log('this item does not exist in the array')
              calcScore();
              return true;
          }
          else if(item === key) {
              // clicked item HAS already been clicked
              console.log('this item is already in the array');
              return false;
          }
      }
     )};

  calcScore() {
      this.setState({
        score: +1,
        topScore: +1,
      })
  }


  render() {
    return (
      <div className="App">
        <Header 
          gameActive={this.state.gameActive}
          score={this.state.score}
          topScore={this.state.topScore}
        />
        <Container 
          checkForDuplicates={this.checkForDuplicates}
        />
      </div>
    );
  }
}

export default App;
