import React, { Component } from 'react';
import './App.css';
import Header from "./components/Header";
import Container from "./components/Container";


class App extends Component {

    constructor(props) {
        super(props)

        // Bind the this context to the handler function
        this.handler = this.handler.bind(this);

        // Set some state
        this.state = {
            gameActive: true,
            score: 0,
            topScore: 0,
            clickedItems: []
        };
    }

    checkForDuplicates(key) {
      this.state.clickedItems.forEach(item => {
          if(item !== key) {
              // clicked item has NOT been clicked before
              console.log('this item does not exist in the array')
              this.logClick(key);
              this.calcScore();
              return true;
          }
          else if(item === key) {
              // clicked item HAS already been clicked
              console.log('this item is already in the array');
              return false;
          }
      }
     )};


    calcScore() {
        this.setState({
          score: +1,
          topScore: +1,
        })
    }

    logClick(key) {
      this.state.clickedItems.push(key)
      // this.setState({
      //   this.state.clickedItems.push(key)
      // })
        console.log(this.state.clickedItems);
    }


  render() {
    return (
      <div className="App">
        <Header 
          gameActive={this.state.gameActive}
          score={this.state.score}
          topScore={this.state.topScore}
        />
        <Container 
          checkForDuplicates={this.checkForDuplicates}
        />
      </div>
    );
  }
}

export default App;
