import React from "react";

export default class Container extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      gameActive: true,
      score: 0,
      topScore: 0
    }
  }

  render() {
    return (
      <div className='container'><Card/></div>
    )
  }
}

let clickedItems = [];

function Card() {
   const images = [
        'https://photo2.foodgawker.com/wp-content/uploads/2019/03/3393004.jpg',
        'https://photo.foodgawker.com/wp-content/uploads/2019/02/3387319.jpg',
        'https://photo.foodgawker.com/wp-content/uploads/2019/02/3384831.jpg',
        'https://photo.foodgawker.com/wp-content/uploads/2019/02/3383607.jpg',
        'https://photo.foodgawker.com/wp-content/uploads/2019/02/3382343.jpg',
        'https://photo.foodgawker.com/wp-content/uploads/2019/02/3380227.jpg',
        'https://photo.foodgawker.com/wp-content/uploads/2019/01/3359963.jpg',
        'https://photo2.foodgawker.com/wp-content/uploads/2018/12/3350447.jpg',
        'https://photo2.foodgawker.com/wp-content/uploads/2019/01/3362759.jpg',
        'https://photo2.foodgawker.com/wp-content/uploads/2018/12/3355340.jpg',
        'https://photo.foodgawker.com/wp-content/uploads/2018/10/3323959.jpg',
        'https://photo2.foodgawker.com/wp-content/uploads/2018/09/3302988.jpg'
    ];

    function handleClick(event, key) {
        event.preventDefault();
        console.log(`The link was clicked:, ${key}`);
        // logClick(key);
        checkForDuplicates(key)
        logClick(key)
          // .then(function(result) {
          //   return checkForDuplicates();
          // })
    }

    function calcScore() {
      this.setState({
        score: +1,
        topScore: +1,
      })
    }


    function checkForDuplicates(key) {
      clickedItems.forEach(item => {
          if(item !== key) {
              // clicked item has NOT been clicked before
              console.log('this item does not exist in the array')

              return true;
          }
          else if(item === key) {
              // clicked item HAS already been clicked
              console.log('this item is already in the array');
              return false;
          }
      }
    )};


    function logClick(key) {
        clickedItems.push(key);
        console.log(clickedItems);
    }

    return images.map((item, key) => (
        <div
            className="card"
            key={key}
            value={item.id}
            onClick={((event) => handleClick(event, key))}
        >
        <img src={item} alt="donut"></img>
        </div>
    ));
}
