import React, { Component } from 'react';
import './App.css';
import Header from "./components/Header";
import Container from "./components/Container";


class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
            gameActive: true,
            score: 10,
            topScore: 0,
            clickedItems: []
        };
    }

  checkForDuplicates(key) {
      console.log(`yay clicked from within app ${key}`);
      runComparison(key);
  }

   runComparison(key) {

      console.log(`runComparison ran ${key}`);

      this.setState({
        score: this.state.score + 1
      })

      console.log(this.state.score);

      this.state.clickedItems.forEach(item => {
          if(this.state.clickedItems.length === 0) {
            return
          }
          else {
              if(item !== key) {
                  // clicked item has NOT been clicked before
                  console.log('this item does not exist in the array')
                  this.logClick(key);
                  this.calcScore();
                  // return true;
              }
              else if(item === key) {
                  // clicked item HAS already been clicked
                  console.log('this item is already in the array');
                  // return false;
              }
          }
      })
  }





    calcScore = () => {
        this.setState({
          score: this.state.score + 1,
          topScore: this.state.topScore + 1,
        })
    }


    logClick = (key) => {
        this.state.clickedItems.push(key)
        console.log(this.state.clickedItems);
    }


    render() {
      return (
        <div className="App">
          <Header 
            gameActive={this.state.gameActive}
            score={this.state.score}
            topScore={this.state.topScore}
          />
          <Container 
            checkForDuplicates={this.checkForDuplicates}
            score={this.state.score}
          />
        </div>
      );
    }
}

export default App;
