import React, { Component } from 'react';
import './App.css';
import Header from "./components/Header";
import Container from "./components/Container";


class App extends Component {
  constructor(props) {
      super(props)
      this.state = {
          gameActive: true,
          score: 0,
          topScore: 0,
          clickedItems: []
      };
      this.checkForDuplicates = this.checkForDuplicates.bind(this);
      this.runComparison = this.runComparison.bind(this);
      this.calcScore = this.calcScore.bind(this);
      this.logClick = this.logClick.bind(this);
  }


  checkForDuplicates(key) {
        this.runComparison(key);
        // this.setState({ score: this.state.score + 1 })
  };

  runComparison = (key) =>{
          console.log(`runComparison ran. Key: ${key}`);
          // console.log(`${this.state.score}`);
          console.log(`${this.state.clickedItems}`);


          this.state.clickedItems.forEach(item => {
              // if(this.state.clickedItems.length === 0) {
              //   return
              // }
              // else {
                  if(item !== key) {
                      // clicked item has NOT been clicked before
                      console.log('this item does not exist in the array')
                      this.logClick(key);
                      this.calcScore();
                      // return true;
                  }
                  else if(item === key) {
                      // clicked item HAS already been clicked
                      console.log('this item is already in the array');
                      // return false;
                  }
              // }
          })
    };

      calcScore = () => {
        this.setState({
          score: this.state.score + 1,
          topScore: this.state.topScore + 1,
        })
      }


      logClick = (key) => {
          this.state.clickedItems.push(key)
          console.log(this.state.clickedItems);
      }



    render() {
      return (
        <div className="App">
          <Header 
            gameActive={this.state.gameActive}
            score={this.state.score}
            topScore={this.state.topScore}
          />
          <Container 
            checkForDuplicates={this.checkForDuplicates}
            score={this.state.score}
          />
        </div>
      );
    }
}

export default App;
