import React, { Component } from 'react';
import './App.css';
import Header from "./components/Header";
import Container from "./components/Container";


class App extends Component {
  constructor(props) {
      super(props)
      this.state = {
          gameActive: true,
          score: 0,
          topScore: 0,
          clickedItems: []
      };
      this.checkForDuplicates = this.checkForDuplicates.bind(this);
      this.runComparison = this.runComparison.bind(this);
      this.calcScore = this.calcScore.bind(this);
  }


  checkForDuplicates(key) {
        this.runComparison(key);
  };

  runComparison = (key) =>{
      console.log(`runComparison ran. Key: ${key}`);

      if(this.state.clickedItems.length === 0) {
        this.state.clickedItems.push(key)
        this.calcScore();
        return 
      }
      else{
        for (let item of this.state.clickedItems) {
          if(item === key) {
              // clicked item HAS already been clicked
              console.log(`this item: ${key} is already in the array`);
              alert('game over');
              this.setState({
                gameActive: false,
                score: 0,
                clickedItems: []
              })
              break
          }
          else if(item !== key){
              // clicked item has NOT been clicked before
              console.log(`this item: ${key} does NOT exist in the array`) 
          } 
          this.calcScore();
        }
      }
      this.state.clickedItems.push(key)
    };

    calcScore = () => {
      if (this.state.score >= this.state.topScore){
        this.setState({
          topScore: this.state.topScore + 1,
        })
      }else if (this.state.score < this.state.topScore) {
        this.setState({
          score: this.state.score + 1,
        })
      }
    }


    render() {
      return (
        <div className="App">
          <Header 
            gameActive={this.state.gameActive}
            score={this.state.score}
            topScore={this.state.topScore}
          />
          <Container 
            checkForDuplicates={this.checkForDuplicates}
            score={this.state.score}
          />
        </div>
      );
    }
}

export default App;
