import React from "react";

export default class Container extends React.Component {
  render() {
    return (
      <div className='container'><Card/></div>
    )
  }
}

function Card() {

   const images = [
        '/assets/blue.png',
        '../src/assets/pink.jpg',
        '../src/assets/plain.png',
        '../src/assets/rasberry.png',
        '../src/assets/sugar.png'
    ];

    function handleClick(event) {
        event.preventDefault();
        let dataid = this.target.dataid;
        console.log(`The link was clicked:, ${dataid}`);
    }

    return images.map((item, key) => (
        <div
            className="card"
            key={key}
            dataid={key}
            style={{backgroundImage: `url(${item})`}}
            onClick={handleClick}
        >
        <img alt="whater" src={item}></img>
        </div>
    ));
}
